!function(){const n=document.querySelector(".pricing-plans__annual-button"),e=document.querySelector(".pricing-plans__annual-button-circle"),c=document.querySelectorAll(".pricing-plans__plan-price");n.addEventListener("click",(()=>{e.classList.toggle("active"),e.classList.contains("active")?c.forEach((n=>{n.innerHTML="$"+12*parseInt(n.innerHTML.slice(1))*.8+"<span>/year</span>"})):c.forEach((n=>{n.innerHTML="$"+parseInt(n.innerHTML.slice(1))/12/.8+"<span>/mo</span>"}))}))}();